import React, { Component } from 'react'
import styles from './styles/Frontend.module.css'
import Image from 'next/image'
import Typewriter from 'typewriter-effect/dist/core'


export default class Home extends Component {

    state = {
        teneez: { display: 'none' },
        teneezscreen: { opacity: 0 },
        placeholder: { opacity: 1 }
    }

    componentDidMount() {

        new Typewriter(document.getElementById('frontendtype'), { delay: 150 })
            .pauseFor(500)
            .typeString('FRONTEND')
            .start();

    }

    toggle_teneez = () => {
        if (this.state.teneezscreen.opacity == 1) {
            console.log("hiding")
            this.setState({
                teneez: { display: 'none' },
                teneezscreen: { opacity: 0 },
                placeholder: { opacity: 1 }
            });
        } else {
            console.log("showing")
            this.setState({
                teneez: { display: 'block', fontSize: '16px' },
                teneezscreen: { opacity: 1 },
                placeholder: { opacity: 0 }
            });
        }
    }

    render() {
        return (
            <div>
                <h1 id='frontendtype' className={styles.title}></h1>
                <div className={styles.dropdown}>
                    <div>
                        <p onClick={this.toggle_teneez} className={styles.expand1}>TENEEZ</p>
                        <p onClick={this.toggle_teneez} className={styles.expand1}>expand</p>
                        <div style={this.state.teneez} className={styles.explanation}>
                            <p>Co-Founder, CTO, Frontend Developer & Designer</p>
                            <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.</p>
                        </div>
                    </div>
                    <div>
                        <p className={styles.expand2}>MUS-ROVER</p>
                        <p className={styles.expand2}>expand</p>
                        <div style={{ display: 'none' }} className={styles.explanation}>
                            <p>Frontend Developer & Designer</p>
                            <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.</p>
                        </div>
                    </div>
                    <div>
                        <p className={styles.expand3}>PERSONAL SITE</p>
                        <p className={styles.expand3}>expand</p>
                        <div style={{ display: 'none' }} className={styles.explanation}>
                            <p>Developer & Designer</p>
                            <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.</p>
                        </div>
                    </div>
                </div>
                <div className={styles.monitor} />

                <div style={this.state.teneezscreen} className={styles.screen}>
                    <Image src="/Teneez.png" layout='fill' alt='teneez' />
                </div>
                <div style={this.state.placeholder} className={styles.placeholder}>
                    <p>select a project</p>
                </div>

            </div>
        )
    }
}
